import { EventId } from '@wca/helpers'

// These will be generated by the swagger file generated with Rswag
interface SubmitRegistrationBody {
  user_id: string
  competition_id: string
  comment: string
  event_ids: EventId[]
}

interface UpdateRegistrationBody {
  user_id: string
  competition_id: string
  status?: string
  event_ids?: EventId[]
  comment?: string
  admin_comment?: string
}

type GetRegistrationBody = Record<string, never>

// This needs to be moved to WCA-helpers
interface CompetitionInfo {
  id: string
  name: string
  registration_open: string
  registration_close: string
  announced_at: string
  start_date: string
  end_date: string
  competitor_limit?: number
  cancelled_at?: string
  url: string
  website: string
  short_name: string
  city: string
  venue_address: string
  venue_details: string
  latitude_degrees: number
  longitude_degrees: number
  country_iso2: string
  event_ids: EventId[]
  // TODO: create a User Type
  delegates: object[]
  organizers: object[]
  class: string
}
